import React, { Component } from 'react';
import TodosListItem from './TodosListItem';
import { connect } from 'react-redux';

class TodosList extends Component {
    constructor(props) {
        super(props);
        this.state = {

        }
    }

    isChange = (event) => {
        let name = event.target.name;
        let value = event.target.value;
        this.setState({
            [name]: value
        })
        console.log(value)
    }

    render() {
        return (
            <div className="col-md-6">
                <div className="todolist not-done">
                    <h1>Todos</h1>
                    .<div class="input-group">
                        <input type="text" name="Name" onChange={(event) => this.isChange(event)} className="form-control add-todo" placeholder="Add todo" />
                        <div className="input-group-append">
                            <button className="btn btn-info">Submit</button>
                        </div>
                    </div>
                    <button id="checkAll" className="btn btn-success">Mark all as done</button>
                    <hr />
                    <ul id="sortable" className="list-unstyled">
                        <TodosListItem></TodosListItem>
                        <TodosListItem></TodosListItem>
                        <TodosListItem></TodosListItem>
                        <TodosListItem></TodosListItem>
                        <TodosListItem></TodosListItem>
                        <TodosListItem></TodosListItem>

                    </ul>
                    <div className="todo-footer">
                        <strong><span className="count-todos" />10</strong> Items Left
    </div>
                </div>
            </div>

        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
        data: state.data
    }
}

const mapDispatchToProps = (dispatch, ownProps) => {
    return {

    }
}

export default connect(mapStateToProps, mapDispatchToProps)(TodosList)
