import { todoListData } from './fireBaseConnect'
let redux = require('redux');



const todoListInitialState = {
    data: []
}
const todoListReducer = (state = todoListInitialState, action) => {
    switch (action.type) {
        default:
            return state
    }
}

const getData = () => {
    let data = [];
    todoListData.on('value', (snapshot) => {
        snapshot.forEach(item => {
            let Id = item.key;
            let Name = item.val().Name;
            let Level = item.val().Level;
            let Status = item.val().Status;
            data.push({
                Id: Id,
                Name: Name,
                Level: Level,
                Status: Status
            });
            this.setState({
                data: data
            })
        })
    })

}

let store = redux.createStore(todoListReducer);
export default store;
