import React, { Component } from 'react';
import TodosListItem from './TodosListItem';
import { connect } from 'react-redux';
import { todoListData } from './fireBaseConnect'


class TodosList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Name: ""
        }
    }


    componentWillMount() {
        todoListData.on('value', (snapshot) => {
            let data = [];
            snapshot.forEach(item => {
                let Id = item.key;
                let Name = item.val().Name;
                let Status = item.val().Status;
                data.push({
                    Id: Id,
                    Name: Name,
                    Status: Status
                });

            });
            this.setState({ data: data });
            this.props.getData(data);
        });


    }


    isChange = (event) => {
        let name = event.target.name;
        let value = event.target.value;
        this.setState({
            [name]: value
        })
    }

    keyPress = (event) => {
        if (event.key === "Enter" && this.state.Name !== "") {
            let item = {
                Name: this.state.Name,
                Status: 1
            }
            this.props.addNew(item)
            this.setState({
                Name: ""
            })
        }
    }

    setData = () => (

        this.state.data.map((item, key) => {
            console.log(item)
            return (<TodosListItem key={key} Name={item.Name}></TodosListItem>)
        })
    )

    render() {
        return (
            <div className="col-md-6">
                <div className="todolist not-done">
                    <h1>Todos</h1>
                    <input type="text" value={this.state.Name} name="Name" onKeyPress={(event) => this.keyPress(event)} onChange={(event) => this.isChange(event)} className="form-control add-todo" placeholder="Add todo" />


                    <button id="checkAll" className="btn btn-success">Mark all as done</button>
                    <hr />
                    <ul id="sortable" className="list-unstyled">
                        {this.setData()}

                    </ul>
                    <div className="todo-footer">
                        <strong><span className="count-todos" />10</strong> Items Left
    </div>
                </div>
            </div>

        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
        data: state.data
    }
}

const mapDispatchToProps = (dispatch, ownProps) => {
    return {
        addNew: (item) => {
            dispatch({ type: "ADD_NEW", newItem: item })
        },
        getData: (data) => {
            dispatch({ type: "GET_DATA", newData: data })
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(TodosList)
